mapper.select:
WITH RECURSIVE category_tree AS ( SELECT category_id FROM category WHERE category_id = #{categoryId} UNION ALL SELECT c.category_id FROM category c INNER JOIN category_tree ct ON c.parent_id = ct.category_id ) SELECT p.product_id, p.name, p.price, p.sales, p.category_id, p.create_time, p.update_time, pi.image_url as image FROM Product p LEFT JOIN ProductImage pi ON p.product_id = pi.product_id AND pi.is_main = true WHERE p.category_id IN (SELECT category_id FROM category_tree)
AND p.price >= #{minPrice}
AND p.price <= #{maxPrice}
ORDER BY p.${sortBy} ${sortOrder}
ORDER BY p.create_time DESC

WITH RECURSIVE category_tree AS ( SELECT category_id FROM category WHERE category_id = #{categoryId} UNION ALL SELECT c.category_id FROM category c INNER JOIN category_tree ct ON c.parent_id = ct.category_id ) SELECT COUNT(*) FROM Product p WHERE p.category_id IN (SELECT category_id FROM category_tree)
AND p.price >= #{minPrice}
AND p.price <= #{maxPrice}

SELECT p.product_id, p.name, p.price, p.sales, p.category_id, p.create_time, p.update_time, pi.image_url as image FROM Product p LEFT JOIN ProductImage pi ON p.product_id = pi.product_id AND pi.is_main = true WHERE 1=1
AND p.price >= #{minPrice}
AND p.price <= #{maxPrice}
ORDER BY p.${sortBy} ${sortOrder}
ORDER BY p.create_time DESC

SELECT COUNT(*)
FROM Product p
WHERE 1=1

    AND p.price >= #{minPrice}


    AND p.price <= #{maxPrice}

SELECT p.product_id AS productId, p.name, p.description, p.price, p.stock, p.sales, p.freight AS shippingFee, p.category_id AS categoryId, p.create_time AS createTime, p.update_time AS updateTime, c.name AS categoryName, (SELECT pi.image_url FROM ProductImage pi WHERE pi.product_id = p.product_id AND pi.is_main = 1 LIMIT 1) AS mainImageUrl FROM Product p LEFT JOIN Category c ON p.category_id = c.category_id
AND p.name LIKE CONCAT('%', #{name}, '%')
AND p.category_id = #{categoryId}
AND p.price >= #{minPrice}
AND p.price <= #{maxPrice}
ORDER BY
p.price p.sales p.create_time
p.create_time
${sortOrder}
ORDER BY p.create_time DESC

SELECT product_id, name, description, price, stock, sales, freight, category_id, create_time, update_time
FROM Product
WHERE product_id = #{id}

mapper.insert:
INSERT INTO Product (name, category_id, price, stock, freight, description, sales

)
VALUES (#{name}, #{categoryId}, #{price}, #{stock}, #{freight}, #{description}, 0

)

mapper.update:
UPDATE Product
name = #{name}, description = #{description}, price = #{price}, stock = #{stock}, freight = #{freight}, category_id = #{categoryId},
WHERE product_id = #{productId}

mapper.select:
SELECT id, product_id AS productId, image_url AS imageUrl, sort_order AS sortOrder, is_main AS isMain, created_time AS createdTime
FROM ProductImage
WHERE id = #{imageId}
